import { useEffect, useState } from "react";
import API from "../services/api";

export default function RegistroPage({ setAuth, isAdmin }) {
  const [registros, setRegistros] = useState([]);

  const getRegistros = async () => {
    try {
      const url = isAdmin ? "/registros/all" : "/registros";
      const res = await API.get(url);
      setRegistros(res.data);
    } catch (err) {
      console.error(err);
      alert("Error al cargar registros");
    }
  };

  // Las funciones entrada y salida solo deberían estar activas para usuarios normales
  // Si quieres, puedes esconder los botones para admin
  const entrada = async () => {
    try {
      await API.post("/registros/entrada");
      getRegistros();
    } catch (err) {
      console.error(err);
      alert("Error al registrar entrada");
    }
  };

  const salida = async () => {
    try {
      await API.post("/registros/salida");
      getRegistros();
    } catch (err) {
      console.error(err);
      alert("Error al registrar salida");
    }
  };

  const logout = () => {
    localStorage.removeItem("token");
    setAuth(false);
  };

  useEffect(() => {
    getRegistros();
  }, []);

  return (
    <div>
      <h2>Registro de horas</h2>

      {!isAdmin && (
        <>
          <button onClick={entrada}>Registrar entrada</button>
          <button onClick={salida}>Registrar salida</button>
        </>
      )}

      <button onClick={logout} style={{ marginLeft: "10px" }}>
        Cerrar sesión
      </button>

      <table border="1" style={{ marginTop: 20 }}>
        <thead>
          <tr>
            {isAdmin && <th>Empleado</th>}
            <th>Fecha</th>
            <th>Entrada</th>
            <th>Salida</th>
          </tr>
        </thead>
        <tbody>
          {registros.map((r) => (
            <tr key={r._id}>
              {isAdmin && (
                <td>{r.usuarioNombre || r.usuarioEmail || "Empleado"}</td>
              )}
              <td>{r.fecha}</td>
              <td>{r.horaEntrada}</td>
              <td>{r.horaSalida || "-"}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
